vec2 power(in vec2 xy,in int powof){
    vec2 z;
    for (int x=0; x<powof-1; x++){
        z = xy *xy;
    }
    return z;
}
vec3 power(in vec3 xy,in int powof){
    vec3 z;
    for (int x=0; x<powof-1; x++){
        z = xy *xy;
    }
    return z;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy -0.5;

    vec3 col = vec3(sin(uv.xyy+vec3(9,21,20)-sin(power(uv.xxy,2)+iTime/3.))) ;
    col*= fract(2.*cos(length(col/0.002)+iTime))-0.6;
    col*= 4.;
    fragColor = vec4(col,1.0);
}
