void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    float ws = 1.0 / iResolution.x;

    float t = iTime / 2.0;
    uint n = uint(t) + 1u;

    float v = 1.0, s = 1.0;

    for(uint i = 1u; i <= n; i++)
    {
        float d = abs(fract(uv.x * s) - 0.5);
        float ft = i == n ? fract(t) : 1.0f;
        float ps = ws * s;

        float a = smoothstep(0.2, 0.28, ft);
        float ab = mix(a, smoothstep(0.65, 1.0, ft), 0.85);
        float vf = smoothstep(-ps, ps, d - ab/6.0);

        v *= mix(1.0, vf, a);
        s *= 3.0;
    }

    fragColor = vec4(vec3(v), 1.0);
}
