//	<https://www.shadertoy.com/view/4dS3Wd>
//	By Morgan McGuire @morgan3d, http://graphicscodex.com
//
float hash(float n) { return fract(sin(n) * 1e4); }
float hash(vec2 p) { return fract(1e4 * sin(17.0 * p.x + p.y * 0.1) * (0.1 + abs(sin(p.y * 13.0 + p.x)))); }

float noise(float x) {
	float i = floor(x);
	float f = fract(x);
	float u = f * f * (3.0 - 2.0 * f);
	return mix(hash(i), hash(i + 1.0), u);
}

float perlin_noise_within_range3(float p, vec2 range) {
    float perlin = noise(p);
    return perlin * (range.y - range.x) + range.x;
}

float cos_in_range(float a, vec2 range) {
    return ((cos(a) + 1.0) * 0.5) * (range.y - range.x) + range.x;
}

#define ENTITY_ID 85983258.0

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    float freq = 1.0 * 3.0;
    float freq2 = 3.0 * 1.0;
    float weight = 0.3;
    float step_cutouff = .2;
    //float x = uv.x;// + iTime / 2.0;
    float x = uv.x + ENTITY_ID + iTime / 1.0;
    vec2 range = vec2(0.25, 0.9);
    step_cutouff = step_cutouff * (range.y - range.x) + range.x;
    float noise1 = cos_in_range(freq * x, range)* weight;
    float pf = perlin_noise_within_range3(x* freq2, range);
    float noise2 =pf * (1.0 - weight) * 1.0;
    float noise = (noise1 + noise2);
    float noise_step = step(noise, step_cutouff);
    if(uv.y < noise_step){
        fragColor = vec4(1,1,1,1);
    } else if (uv.y < noise && uv.y > range.x) {
        fragColor = vec4(0, 1, 0, 1);
    } else if (uv.y < range.x || uv.y > range.y) {
        fragColor = vec4(1, 0, 1, 1);
    }
}
