#define PI 3.141592653589
#define PointSize .001
#define Divisor 2.5
vec2 DividedVec(vec2 a,vec2 b, float divisor){
    vec2 c = vec2((a.x + b.x)/divisor,(a.y + b.y)/divisor);
    return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy -.5;
    uv.x *= iResolution.x/iResolution.y;
    uv *=1.;
    vec3 col;
    vec2 p1,p2,p3,p4,p5;
    float theta = (2. * PI) / 2. * cos(iTime * 0.1);
    p1 = vec2(.5 * cos(theta * 0.),.5 * sin(theta * 0.));
    p2 = vec2(.5 * cos(theta * 1.),.5 * sin(theta * 1.));
    p3 = vec2(.5 * cos(theta * 2.),.5 * sin(theta * 2.));
    p4 = vec2(.5 * cos(theta * 3.),.5 * sin(theta * 3.));
    p5 = vec2(.5 * cos(theta * 4.),.5 * sin(theta * 4.));

    float a =length(uv - p1);
    float b =length(uv - p2);
    float c =length(uv - p3);
    float d =length(uv - p4);
    float e =length(uv - p5);

    if(a<PointSize||b<PointSize||c<PointSize||d<PointSize||e<PointSize){
    col.b =1.;
    }
    vec2 StartingPoint = vec2(.5 * cos(iTime),.5 * sin(iTime));
    if(length(uv - StartingPoint) <PointSize){
        col.g = 1.;
    }
    vec2 C;
    for(float i = 0.;i<2000.;i++){
    float RandomNumber =  2. * sin(i  * iTime *sin(i * iTime * sin(i)));

    if(RandomNumber > 1.5){
        C = DividedVec(StartingPoint,p1,Divisor);
    }
     if(RandomNumber > .5 && RandomNumber < 1.5){
        C = DividedVec(StartingPoint,p2,Divisor);
    }
     if(RandomNumber< -.5 && RandomNumber > -1.5){
        C = DividedVec(StartingPoint,p3,Divisor);
    }
     if(RandomNumber < -1.5){
        C = DividedVec(StartingPoint,p4,Divisor);
    }
     if(RandomNumber > -.5&& RandomNumber < .5){
        C = DividedVec(StartingPoint,p5,Divisor);
    }

    if(length(uv - C) < PointSize){
        col.g = 1.;
    }
        StartingPoint = C;
    }
    fragColor = vec4(col,1.0);
}
