float lerp(float x, float y, float a) { return (y-x)*a + x; }
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)

    // Time varying pixel color

    vec3 res = iChannelResolution[0];

    vec2 final = fragCoord/res.xy;
    vec4 sampleThing = texture(iChannel1,vec3(fragCoord/iResolution.xy,1));
    vec2 offset;
    float chosen = sampleThing.x;
    offset.x = lerp(iTime,iTime * chosen,0.0075);
    offset.y = 0.0;

    vec2 uv = final + offset;

    // Output to screen
    vec4 multi = texture(iChannel0, uv);
    fragColor = vec4(multi.x,multi.x,multi.x ,1.0);
}
