#define rot(a) mat2(cos(a + vec4(0,11,33,0))) 
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y/5.;
    float zs = 70. * iResolution.y/360.;

    vec3 p = vec3(1., uv*zs), g=p;   

    p.xy *= rot(iTime/33.);
    p.xz *= rot(iTime/21.);

    g.xy *= rot(iTime/29.);
    g.xz *= rot(iTime/47.);

    float col = dot(sin(p) * cos(p).yzx, vec3(1.))
              + dot(sin(g) * cos(g).yzx, vec3(1.));

    col = abs(mod(col,.4)-.2)*5.;
    col = smoothstep(0.4, 1.0, col);

    fragColor = vec4(vec3(col), 1.);
}
