#define R iResolution
#define T iTime/5.0
void mainImage( out vec4 o, in vec2 p )
{
    vec2 u = (p*2.0-R.xy)/R.x;
    float l = pow(length(u),0.5);
    float l0 = l;
    l -= fract(T)/5.0;
    float tt = T * sign(sin(20.0*l*acos(0.0)));
    u *= mat2(cos(tt), -sin(tt), sin(tt), cos(tt));
    l = l - floor(l*10.0) / 10.0;
    l = l > 0.01 ? pow(l, 0.15):0.0;
    l *= l0;
    float t = atan(u.y,u.x)/3.1415;
    t = abs(t-floor(t*20.0)/20.0);
    t = clamp(t,0.0,0.05)*20.0;
    t = t>0.05?pow(t,0.15):0.0;
    o = vec4(t*l,0.,0.,1.0);
}
