float Background(vec2 uv)
{
    float Reaction = cos(iTime * BPM * pi);

    uv *= mat2(cos(-.2), -sin(-.2), sin(-.2), cos(-.2));
    uv *= 8. + Reaction;
    uv += iTime * 5. + Reaction * .8;
    uv = mod((uv + .5), vec2(4.2, 1.)) - .5;
    float In[14] = float[14](
        67.,
        72.,
        73.,
        80.,
        32.,
        69.,
        78.,
        71.,
        73.,
        78.,
        69.,
        32.,
        73.,
        73.
    );

    float Color = 0.;
    Text(uv, vec2(.5), vec2(0.), In, Color)

    return Color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - iResolution.xy * .5)/iResolution.y;

    float Col = TriangleWave(uv, vec2(.5, .25), vec2(-.25, .375));
    Col += SquareWave(uv, vec2(.5, .25), vec2(-.25, .125));
    Col += SawWave(uv, vec2(.5, .25), vec2(-.25, -.125));
    Col += PulseWave(uv, vec2(.5, .25), vec2(-.25, -.375));
    Col += FM1(uv, vec2(.5, .25), vec2(.25, .375));
    Col += FM2(uv, vec2(.5, .25), vec2(.25, .125));
    Col += WhiteNoise(uv, vec2(.5, .25), vec2(.25, -.125));
    Col += Background(uv) * .2;
    Col += Box(uv);

    fragColor = vec4(vec3(Col), 1.);
}

vec2 mainSound( int samp, float time )
{
    float At = mod(time * BPM, 8.);

    int PatternAt = int(mod(time * BPM / 8., Total));

    float Sum = TriangleTrack(At + TrianglePatterns[PatternAt] * 8., 0., 0.);
    Sum += SquareTrack(At + SquarePatterns[PatternAt] * 8., 0., 0.) * .5;
    Sum += SawtoothTrack(At + SawtoothPatterns[PatternAt] * 8., 0., 0.);
    Sum += PulseTrack(At + PulsePatterns[PatternAt] * 8., 0., 0.) * .5;
    Sum += FM1Track(At + FM1Patterns[PatternAt] * 8., 0., 0.);
    Sum += FM2Track(At + FM2Patterns[PatternAt] * 8., 0., 0.) * .75;
    Sum += WhiteNoiseTrack(At + WhiteNoisePatterns[PatternAt] * 8., 0., 0.) * .5;

    return vec2(Sum * .2);
}

const float BPM = 150. / 60.; //BPM / minute

//Use Note * Octave(n)
#define C  16.35
#define CS 17.32
#define D  18.35
#define DS 19.45
#define E  20.60
#define F  21.83
#define FS 23.12
#define G  24.50
#define GS 25.96
#define A  27.50
#define AS 29.14
#define B  30.87

#define pi 3.14159

#define Octave(Octave) pow(2., Octave)
#define Pattern(P, T) P * 8. + T
#define Lerp(a, b, t) (a - (a - b) * clamp(t, 0., 1.))
#define Transition(t) smoothstep(.0, .005, t) * smoothstep(1., 1. - .005, t)

//ty ttg#1273 for helping me fix the distortion problem!
float GetWaveTime(float Time, float Duration, float StartHz, float GoalHz) {
    float Freq = StartHz + (StartHz - GoalHz) * (-Time * .5);
    return fract(Time * Duration / BPM * Freq);
}

float TriangleWave(float Time, float Volume) {
    return abs(fract(Time) - .5) * -Volume * 4. + Volume;
}
float TriangleNote(float At, float DisAt, float DisMult, float NoteAt, float Duration, float StartHz, float StartVolume, float GoalHz, float GoalVolume)
{
    float Time = (At - NoteAt) / Duration;

    return TriangleWave(
        Lerp(
            GetWaveTime(Time, Duration, StartHz, GoalHz), //Sound
            fract(DisAt * Lerp(StartHz, GoalHz, Time)), //Visual
            DisMult //Toggle
        ),
        Lerp(StartVolume, GoalVolume, Time) * Transition(Time)
    );
}

const float Round = 100.;
const float Round1 = 1. / Round;
const float Round2 = 1. + Round1;
float SquareWave(float Time, float Volume) {
    Time *= 2.;
    return cos(clamp(Time, 0., Round1) * pi * Round) * -cos(clamp(Time, 1., Round2) * pi * Round) * Volume;
}
float SquareNote(float At, float DisAt, float DisMult, float NoteAt, float Duration, float StartHz, float StartVolume, float GoalHz, float GoalVolume)
{
    float Time = (At - NoteAt) / Duration;

    return SquareWave(
        Lerp(
            GetWaveTime(Time, Duration, StartHz, GoalHz), //Sound
            fract(DisAt * Lerp(StartHz, GoalHz, Time)), //Visual
            DisMult //Toggle
        ),
        Lerp(StartVolume, GoalVolume, Time) * Transition(Time)
    );
}
