float SawtoothWave(float Time, float Volume) {
    return (cos(clamp(Time, 0., .0125) * pi * 80.) * .5 + .5 + fract(Time)) * Volume * 2. - Volume;
}
float SawtoothNote(float At, float DisAt, float DisMult, float NoteAt, float Duration, float StartHz, float StartVolume, float GoalHz, float GoalVolume)
{
