float PulseWave(float Time, float Volume) {
    Time *= 2.;
    Time = mod(Time + cos(Time * pi) * .6, 2.);
    return cos(clamp(Time, 0., Round1) * pi * Round) * -cos(clamp(Time, 1., Round2) * pi * Round) * Volume;
}
float PulseNote(float At, float DisAt, float DisMult, float NoteAt, float Duration, float StartHz, float StartVolume, float GoalHz, float GoalVolume)
