float FM1Wave(float Time, float Volume) {
    float Carrier = Time * 2.;
    float Mod1 = sin(Carrier * .5 * pi) * .2;
    float Mod2 = sin((Carrier * 2. + Mod1) * pi * 2.) * .08;
    float Sum = (Carrier + Mod1 + Mod2);
    return sin((Sum + sin(Sum * pi * 2.) * .15) * pi * 2.) * Volume;
}
float FM1Note(float At, float DisAt, float DisMult, float NoteAt, float Duration, float StartHz, float StartVolume, float GoalHz, float GoalVolume)
{
