//Modified version of shadertoy.com/view/4djSRW for frequency control
float WhiteNoise(float Time, float Volume) {
    vec2 p = floor(iSampleRate * vec2(Time, Time * 1.423));

    vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return (dot(vec2(1.), fract((p3.xx+p3.yz)*p3.zy)) - 1. ) * Volume ;
}
float WhiteNoiseNote(float At, float DisAt, float DisMult, float NoteAt, float Duration, float StartHz, float StartVolume, float GoalHz, float GoalVolume)
{
    float Time = (At - NoteAt) / Duration;
    float Freq = (StartHz + (StartHz - GoalHz) * (-Time * .5));

    return WhiteNoise(
        Lerp(
            Time * Duration / BPM * Freq, //Sound
            fract(DisAt * Lerp(StartHz, GoalHz, Time)), //Visual
            DisMult //Toggle
        ),
        Lerp(StartVolume, GoalVolume, Time) * Transition(Time)
    );
}

#define PatternCount 52
#define Total 52.
const float TrianglePatterns[PatternCount] = float[PatternCount](
    1., 2., 1., 2.,
    1., 2., 1., 2.,
    1., 2., 1., 2.,
    1., 2., 1., 2.,

    1., 2., 1., 2.,
    1., 2., 1., 2.,
    1., 2., 1., 2.,
    1., 2., 1., 2.,

    1., 2., 1., 2.,
    1., 2., 1., 2.,
    1., 2., 1., 2.,
    1., 2., 1., 2.,

    1., 2., 1., 2.
);
float TriangleTrack(float At, float DisAt, float DisMult)
{
    float Sum = 0.;

    //Pattern1
    Sum += TriangleNote(
        At,
        DisAt,
        DisMult,

        Pattern(1., 2.),
        .25,
        C  * Octave(5.), 1.,
        C  * Octave(2.), 1.
    );
    Sum += TriangleNote(
        At,
        DisAt,
        DisMult,

        Pattern(1., 6.),
        .25,
        C  * Octave(5.), 1.,
        C  * Octave(2.), 1.
    );
    Sum += TriangleNote(
        At,
        DisAt,
        DisMult,

        Pattern(1., 7.),
        .25,
        C  * Octave(5.), 1.,
        C  * Octave(2.), 1.
    );

    //Pattern2
    Sum += TriangleNote(
        At,
        DisAt,
        DisMult,

        Pattern(2., 2.),
        .25,
        C  * Octave(5.), 1.,
