float noise(vec2 p) {
    return texture(iChannel0,p).r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.x;

    uv*=0.25;

    float dx = 0.00097;

    float up = noise(uv+vec2(0,dx));
    float dn = noise(uv+vec2(0,-dx));
    float lf = noise(uv+vec2(-dx,0));
    float rt = noise(uv+vec2(dx,0));
    float me = noise(uv)+iMouse.x*0.0028-1.0; // drag your mouse left and right

    bool best = false;
    if(me > up && me > dn && me > lf && me > rt) {
        best = true;
    }


    float set = noise(vec2(noise(uv),noise(uv)+0.01));
    //set = min(set,1.0);

    // Time varying pixel color
    vec3 col;
    //vec3 col = vec3((up+dn+lf+rt+noise(uv))/5.0); // noise(uv)

    if(set < 2.0 && best) col = vec3(1.0);

    // Output to screen
    fragColor = vec4(col,1.0);
}
