void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 o_trpino = fragCoord/iResolution.xy;
    float n_scanlines = 66.;
    //n_scanlines = iResolution.y/2.;
    vec2 o_subdivs = vec2(1.,n_scanlines);
    vec2 o_trpi_big = o_trpino * o_subdivs;
    vec2 o_trpi_big_fract = fract(o_trpi_big);
    vec4 o_col = texture(iChannel0, o_trpino);
    if(o_trpi_big_fract.y > (1./2.)){
        o_col = texture(iChannel0, o_trpino);

    }else{
        o_col = texture(iChannel1, o_trpino);

    }
    // Output to screen
    fragColor = o_col;
}

