void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy * 2.0 - 1.0;

    float theta = 0.02 * iTime;

    for (float i = 0.0; i < 100.0; i += 1.0){
        uv = abs(uv);
        uv -= vec2(0.5 + 0.25 * sin(iTime * 0.5), 0.5 + 0.25 * cos(iTime * 0.3));
        uv *= 1.1;
        uv *= mat2(
            cos(theta), -sin(theta),
            sin(theta), cos(theta)
        );
    }

    fragColor = vec4(
        length(uv + vec2(0.1, 0.273) * sin(iTime * 0.125)),
        length(uv + vec2(0.428, 0.27333) * sin(iTime * 0.625)),
        length(uv + vec2(0.555, 0.555) * sin(iTime * 0.0125)),
        1.0
    );
}
