#define SQRT_HALF 0.7071067811865476

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 uv_centered = uv - vec2(0.5);
    float r = length(uv_centered);
    float th = atan(uv_centered.y , uv_centered.x);



    vec2 mouse_position = iMouse.xy / iResolution.xy - vec2(0.5);
    float mouse_theta = atan(mouse_position.y , mouse_position.x);
    float swirl_intensity = -2.2 * mouse_theta;

    float th_distort = th + swirl_intensity * (0.5 - r);

    vec2 sample_pt = r * vec2(cos(th_distort), sin(th_distort)) + vec2(0.5);


    if(r > 0.5)
    {
        fragColor = vec4(0.0, 0.0, 0.0, 1.0);
    }
    else
    {
        fragColor = texture(iChannel0, sample_pt);
    }

}
