float Hash21(vec2 p) {
    p = fract(p*vec2(123.234,234.345));
    p += dot(p, p+234.43);
    return fract(p.x*p.y);
}

vec4 Truchet(vec2 p, vec3 col) {
    vec2 id = floor(p);
    float n = Hash21(id);

    p = fract(p)-.5;
    //vec3 col = vec3(0);

    float d = 0.;

    float mypx = p.x;
    if (n<.5)
    {
        p.x *= -1.;
    }

    float s = p.x>-p.y ? 1. : -1.;

    float cd = length(p-vec2(0.5)*s);

    float w = 0.01;
    col *= smoothstep(w, -w, abs(cd-.5)-.03);

    //if(mypx>.49||p.y>.49)
    //{
        //col = vec3(1.);
        //d = 1.;
    //}



    return vec4(col, d);
}

float pulse(float tOffset) {
    return 0.5 + cos(iTime + tOffset);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    uv *= 6.;

    uv.x += iTime;


    // Time varying pixel color
    float pi = 3.14159265;
    vec3 col = vec3(0);
    vec4 t1 = Truchet(uv, vec3(pulse(0.0),0.,0.));
    vec4 t2 = Truchet(uv+.5, vec3(0,pulse(pi / 3.0),0));
    vec4 t3 = Truchet(uv-vec2(0.25,0.5), vec3(0,0,pulse(2.0 * pi / 3.0)));

    col = t1.rgb;
    col+= t2.rgb;
    col+= t3.rgb;

    // Output to screen
    fragColor = vec4(col,1.0);
}
