const float preAmnt = 0.95;
const float camAmnt = 1.0 - preAmnt;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    vec4 camCol = texture(iChannel0, uv);
    vec4 preCol = texture(iChannel1, uv);
    fragColor = (preCol * preAmnt + camCol * camAmnt);
}
