float steps = 10.0;
float offset = 1.2;
float xMirror = 0.2;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    uv.x = uv.x > xMirror ? xMirror - (uv.x - xMirror) : uv.x;
    uv.x *= abs(tan(uv.x + offset)) + 1.0;
    uv.x += iTime * 0.2;

    vec2 uvMod = floor(mod(uv * steps, 2.0));

    vec3 col = vec3(uvMod.x * uvMod.y + (1.0 - uvMod.x) * (1.0 - uvMod.y));
    col = clamp(col, 0.15, 0.9);

    // Output to screen
    fragColor = vec4(col,1.0);
}
