float box(vec2 s, vec2 b) {
    vec2 q = abs(s) - b;
    float inside = min(max(q.x, q.y), 0.0);
    float outside = length(max(q, 0.0));

    return inside + outside;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.0 * fragCoord - iResolution.xy)/iResolution.y;


    vec2 p = uv - vec2(iTime * 0.25);

    vec2 modulo = vec2(0.5);
    vec2 halfModulo = modulo * 0.5;
    p = mod(p + halfModulo, modulo) - halfModulo;

    float skew = sin(iTime) * 0.5;
    p *= mat2(
        1, -sin(skew),
        -sin(skew), 1
    );
    float d = box(p, vec2(0.1));


    vec3 col = vec3(-sign(d));
    fragColor = vec4(col,1.0);
}
