{
    vec2 uv = fragCoord / iResolution.xy*2.-1.;

    uv.x *= iResolution.x/iResolution.y;

    vec2 uv2 = uv*1.;
    vec3 fcol = vec3(.0);

    for(float i=.0;i<3.5;i++){
    uv *= 2.;
    uv = fract(uv/1.5);

    uv -= .5;

    //uv -= .5;
    //uv *= 2.;

    //vec3 col = palette(d);

    float d = length(uv)+exp(-length(uv2));

    vec3 col = palette(length(uv2)+ i*.4 + iTime/4.)/10.;

    d -= .5;
