float PIXEL = 5.;


/* uv from -1 to 1 but stepped by a pixel resolution*/
/* horizontally goes further than -1, 1 because it's wider */
vec2 pixelCoord(vec2 fragCoord) {
  int ix = int(floor(fragCoord.x / PIXEL));
  int iy = int(floor(fragCoord.y / PIXEL));
  float scaling = iResolution.y / PIXEL;
  vec2 center = vec2(iResolution.x / iResolution.y, 1.0);
  float x = float(ix)/scaling*2.0 - center.x;
  float y = float(iy)/scaling*2.0 - center.y;
  return vec2(x,y);
}


/* noise and hash from https://www.shadertoy.com/view/4dS3Wd */
float hash(float p) { p = fract(p * 0.011); p *= p + 7.5; p *= p + p; return fract(p); }
float noise(float x) {
    float i = floor(x);
    float f = fract(x);
    float u = f * f * (3.0 - 2.0 * f);
    return mix(hash(i), hash(i + 1.0), u);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = pixelCoord(fragCoord);

    float bg = 0.9*step(0.4,noise((uv.y-floor(iTime*2.)/PIXEL/2.)*1132.23))+0.7;


    vec3 col = bg*vec3(0.8);

    fragColor = vec4(col,1.0);
}
