// based on code-golfed code by FabriceNeyret
void mainImage( out vec4 O, vec2 u )
{
    float t = fract(iTime);
    vec2  R = iResolution.xy,
          U = ceil(32.* ( u+u - R ) / length(R) ) -.5;
    O = vec4(
      mod(
        floor(
          fract(
            fract(
              sin(dot(U+t, R-17.)) * 1e4
            ) / 4. // randomizer strength set to 0.25 here
            + .3 * log(length(U))
            + atan(U.y,U.x) / 6.3 // we can get away with an imprecise value of 2Ï€
            + t
          ) * vec3(2, 4, 8) // 0-7 integer to RGB triplet
        )

        , 2.
      )
      , 1
    );
}
