void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy -0.5;
    vec3 col = vec3(iTime/1.5); //speed

    vec3 curr_col = vec3(0.0);
    curr_col -= length(uv.x+2.0*sin(uv.xyyy))*20.0; // ring shapes

    col += curr_col * vec3(0.1, 0.1,1.); // play with these values for different colors/rings
    col = fract(col)/1.1; // and here for size and brightness


    //col = step(0.8,col);

    fragColor = vec4(col,1.0);
}
