void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord - .5*iResolution.xy)/iResolution.y;
    vec2 m = (iMouse.xy - .5*iResolution.xy)/iResolution.y;

    vec2 a = vec2(0.);
    vec2 b = m;
    vec2 u = uv-a;
    vec2 v = b-a;
    vec2 q = a+v*clamp(dot(u, v)/dot(v, v), 0., 1.);
    float d = length(uv-q);

    // Time varying pixel color
    vec3 col = vec3(0.);//0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    col += step(d, .1+.05*sin(iTime + 10.*uv.x));
    col *= 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    //col += length(uv-b);

    // Output to screen
    fragColor = vec4(col,1.0);
}
