void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord - iResolution.xy)/iResolution.y;
    vec3 finalcol = vec3(0.);
    for (int fs=1;fs<3;fs++) {
        vec3 col = vec3(0.);
        vec2 fuv = uv/float(fs); // fractal uv
        if (fs==2) fuv.y *= -1.;
        for (int i=0; i<8; i++) {
            // fuv *= Rot(sin(iTime*0.1)*6.28);
            float f1 = (smoothstep(0.8,1.,fract(iTime))+floor(iTime))*0.5;
            float steptime = mix(f1,iTime+0.25,0.0);
            steptime *= PI;
            steptime /= 4.;
            fuv *= Rot(steptime);
            fuv.y += 0.05;
            fuv = abs(fuv);
        }

        col.r = smoothstep(0.,.5,length(fuv-0.25-sin(iTime*0.11)*0.1));
        col.r *= mix(1.,smoothstep(-1.,-0.95,sin(fuv.y*20.-iTime)),sin(iTime+fuv.x));
        col.r = sin(col.r+5.);
        col.r *= col.r;
        for (int i=0; i<8; i++) {
            fuv *= Rot(sin(iTime+0.25));
            fuv.y += 0.05;
            fuv = abs(fuv-0.5);
        }

        col.g = smoothstep(0.15,0.3,length(fuv-0.25-sin(iTime*0.12)*0.1));
        for (int i=0; i<8; i++) {
            fuv *= Rot(-0.1*iTime+0.25);
            fuv.y += 0.5;
            fuv = abs(fuv-0.5);
        }

        col.b = smoothstep(0.,0.3,length(fuv-0.25-sin(iTime*0.13)*0.1));
        col.b += col.g*smoothstep(.1,0.9,length(fuv));
        col.b -= length(fuv);

        col = mix(col,0.5*col,smoothstep(1.6,1.1,col.r+col.g+col.b));
        col += col.r * 0.5;
        col *= 1.-smoothstep(0.4,1.,length(uv)/float(fs));
        finalcol += col;
    }
    finalcol.g *= finalcol.r;

    // Output to screen
    fragColor = vec4(finalcol,1.0);
}

#define TAU 6.283185307
#define PI 3.1415926535

float nsin(float a){return sin(a)*0.5+0.5;}

float rand(vec2 n) { 
	return fract(sin(dot(n, vec2(12.9898, 4.1414))) * 43758.5453);
}

bool nearly(in float a, in float b) {
    return abs(a - b) < 0.001;
}

mat2 Rot(in float a) {
    return mat2(cos(a),-sin(a),
                sin(a),cos(a));
}
