void mainImage( out vec4 c, in vec2 f)
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 r = iResolution.xy, u = (2. * f-r)/r.y;

    float a = iTime * 0.5, ca = cos(a), sa = sin(a);
    u *= mat2(ca, -sa, sa, ca);
    float rad = length(u);
    float ang = (atan(u.y, u.x) + radians(180.)) /radians(360.);


    vec2 polar = vec2(rad, ang) +iTime * 0.01;
    // Output to screen
    c = texture(iChannel0,floor(polar * 20.)/20.) * 1.-rad;

}
