// original: https://www.shadertoy.com/view/4djSRW
float f_n_rand(float p){
    p = fract(p * .1031);
    p *= p + 33.33;
    p *= p + p;
    return fract(p);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    vec2 o_p_mouse_data = vec2(0.0, 0.0);

    // Normalized pixel coordinates (from 0 to 1)
    vec2 o_pixel_coo_nor = (fragCoord.xy-iResolution.xy*0.5)/iResolution.y;
    vec2 o_mouse_coo_nor = (iMouse.xy-iResolution.xy*0.5)/iResolution.y;


    if(iMouse.z == 0.0){
        o_mouse_coo_nor = vec2(
            sin(iTime+f_n_rand(iTime)*0.1)*0.5,
            cos(iTime+f_n_rand(iTime)*0.1)*0.5
        );

    }
    //if(fragCoord == o_p_mouse_data){
    if(ivec2(fragCoord.xy) == ivec2(o_p_mouse_data)){
        fragColor = vec4(o_mouse_coo_nor, 0.0, 0.0);
    }else{


        vec4 o_mosue_coo_nor_last = texture(iChannel0, o_p_mouse_data);
        vec2 o_mosue_coo_nor_delta = abs(o_mouse_coo_nor.xy - o_mosue_coo_nor_last.xy);

        float n_length = length(o_mouse_coo_nor-o_pixel_coo_nor);
        // Output to screen
        float n_len_mouse_delta = length(o_mosue_coo_nor_delta);
        float n_size = 0.001+pow(n_len_mouse_delta, 1./1.4);
        float n_size_min = o_mosue_coo_nor_delta.x;
        float n_size_t = sin(iTime*2.)*(n_size/2.)+(n_size/2.)+n_size_min;
        n_length = smoothstep(n_size*2., n_size, n_length);

        vec4 o_col_last = texture(iChannel0, uv);
        fragColor = mix(o_col_last, (vec4(n_length)), n_length);

    }

}
