void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec3 finalColor = vec3(1.0);
    vec3 main_color = vec3(0.0, 1.0, 0.0);
    //vec3 main_color = vec3(0.0, 1.0, 1.0);

    bool zero[100] = bool[100](
    false, false, false, false, false, false, false, false, false, false,    
    false, true , true , true , true , true , true , true , true , false,
    false, true , true , false, false, false, false, true , true , false,
    false, true , true , false, false, false, false, true , true , false,
    false, true , true , false, true , true , false, true , true , false,
    false, true , true , false, true,  true , false, true , true , false,
    false, true , true , false, false, false, false, true , true , false,
    false, true , true , false, false, false, false, true , true , false,
    false, true , true , true , true , true , true , true , true , false,
    false, false, false, false, false, false, false, false, false, false
    );
    bool one[100] = bool[100](
    false, false, false, false, false, false, false, false, false, false,    
    false, false, false, false, false, true , true , true , true , false,
    false, false, false, false, false, false, true , true , true , false,
    false, false, false, false, false, false, true , true , true , false,
    false, false, false, false, false, false, true , true , true , false,
    false, false, false, false, false, false, true , true , true , false,
    false, false, false, false, false, false, true , true , true , false,
    false, false, false, false, false, false, true , true , true , false,
    false, false, false, false, false, true , true , true , true , false,
    false, false, false, false, false, false, false, false, false, false
    ); 

    int num = cos(fragCoord.x-mod(fragCoord.x, 10.0)) > 0.0 ? 1 : 0;

    int fragIndex_X = int(mod(fragCoord.x, 10.0));
    int fragIndex_Y = int(mod(fragCoord.y + iTime * 60.0 , 10.0));

    if(num == 0){
        finalColor = zero[fragIndex_X + fragIndex_Y * 10] ? main_color : vec3(0.0);
    }
    else{
        finalColor = one[fragIndex_X + fragIndex_Y * 10] ? main_color : vec3(0.0);
    }
    if(
    (uv.x > 0.40 && uv.x < 0.59 && uv.y > 0.43 && uv.y < 0.53) || (uv.y > 0.30 && uv.y < 0.66 && uv.x > 0.47 && uv.x < 0.52)||
    (uv.x > 0.73 && uv.x < 0.92 && uv.y > 0.43 && uv.y < 0.53) || (uv.y > 0.30 && uv.y < 0.66 && uv.x > 0.80 && uv.x < 0.85)||
    (pow(uv.x-0.16, 2.0)/pow(0.12, 2.0) + pow(uv.y-0.5, 2.0)/pow(0.3, 2.0) <= 1.0)&&
    !(pow(uv.x-0.16, 2.0)/pow(0.06, 2.0) + pow(uv.y-0.5, 2.0)/pow(0.15, 2.0) <= 1.0)&&
    !(uv.x > 0.16 && uv.x < 0.30 && uv.y > 0.40 && uv.y < 0.6)
    ){
      finalColor*=0.5+0.5*cos(iTime*10.0)+0.2;
    }


    // Output to screen

    fragColor = vec4(finalColor, 1.0);

}
