void mainImage(out vec4 fragColor, in vec2 fragCoord){
    vec2 coord = 5.0 * fragCoord.xy / iResolution.xy;
    vec3 color = vec3(0.2275, 0.0902, 0.8471);

    color *= sin(coord.x * cos(iTime / 60.0) * 20.0) + sin(coord.y * cos(iTime / 60.0) * 21.0);
    color *= sin(coord.x * sin(iTime / 60.0) * 10.0) + sin(coord.y * cos(iTime / 60.0) * 10.0);

    color /= sin(iTime / 10.0) * 0.5;

    fragColor = vec4(color, 1.0);
}
