void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;


	float minDistance = 100.0f;

	for(float i = 0.0; i < 100.0; i++){
		float x = cos(fract(cos(iTime+i)))/5.0;
		float y = cos(fract(cos(iTime+i)))/5.0;
		if(length(vec2(fract(sin(i)*45642.0)+x, fract(sin(i)*8412.0)+y) - uv) < minDistance){
			minDistance = length(vec2(fract(sin(i)*45642.0)+x, fract(sin(i)*8412.0)+y) - uv);
		}
	}


	vec3 water = mix(vec3(0.0f, 0.6, 1.0f), vec3(1.0f, 1.0f, 1.0f), minDistance*6.0);
    vec3 lava = mix(vec3(1.0f, 0.0, 0.0f), vec3(1.0f, 1.5f, 0.0f), minDistance*5.0);

    if(fragCoord.x > iResolution.x / 2.0){
        fragColor = vec4(water,1.0);
    }else{
        fragColor = vec4(lava,1.0);
    }

}
