#define PI 3.1415926538
#define PI2 6.2831853076

float fn1(float x) {
    return sin(x);
    //return x;
}

float fn2(float x) {
    return cos(x)*1.1;
}

float fn3(float x) {
    return cos(x*4.)+cos(x)*2.;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy * 2. - 1.;
    vec2 fCoord = floor(fragCoord);
    vec3 fResHalf = floor(iResolution*0.5);
    float xyScale = iResolution.x/iResolution.y;
    float scale = 4.;
    float solidWidth = 0.0;
    float smoothWidth = 1.0;

    if (fCoord.x == fResHalf.x || fCoord.y== fResHalf.y) {
        fragColor = vec4(1,0.5,1,1.0);
        return;
    }

    float tScale1 = (1.+sin(iTime*2.)*0.5);
    float deltaY1 = abs(fn1(uv.x*xyScale*scale+iTime) - (uv.y*scale*tScale1));
    float di1 = 0.4*(1. - smoothstep(solidWidth, solidWidth + smoothWidth, deltaY1));

    float tScale2 = (1.5+sin(iTime*8.)*0.8);
    float deltaY2 = abs(fn2(uv.x*xyScale*scale-iTime*4.) - ((uv.y-0.2)*scale*tScale2));
    float di2 = 0.7*(1. - smoothstep(solidWidth, solidWidth + smoothWidth, deltaY2));

    float tScale3 = (1.5+sin(iTime*8.)*0.4 + sin(iTime*2.)*0.4);
    float deltaY3 = abs(fn3(uv.x*xyScale*scale-sin(iTime*4.)*2.) - ((uv.y+0.4)*scale*tScale3));
    float di3 = 0.5*(1. - smoothstep(solidWidth, solidWidth + smoothWidth, deltaY3));

    float di = di1+(1.-di1)*di2+(1.-di1)*(1.-di2)*di3;

    float t = (1.+sin(iTime*2.)*sin(iTime*5.)*sin(iTime*0.5))*0.1;
    vec3 col = vec3(di, t + di*0.9, t + di*0.9 - uv.y);

    // Output to screen
    fragColor = vec4(col,1.0);

}
