void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   vec2 uv=(fragCoord-.5*iResolution.xy)/iResolution.y*2.5;
    vec3 col=vec3(0);
    float x=uv.x*1., y=uv.y, t=iTime/2., c=1./x/y;
    for(float i=-5.; i<5.; i=i+.12)
  { 
    float h=tan(c*i*.255+t), v=sin(c*i+t);
    if (x+.1>h && x-.1<h && y+.1>v && y-.1<v)
    col += vec3(1,0,0);
    if (-x+.1>h && -x-.1<h && y+.1>v && y-.1<v)
    col += vec3(.1,1.,.1);

  }
    fragColor = vec4(col,1.);
}
