int gcd(int a, int b)
{
    if(a < b) {
        int t = a;
        a = b;
        b = t;
    }

    while(b > 0) {
        int t = b;
        b = a % b;
        a = t;
    }
    return a;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    int a = int(fragCoord.x);
    int b = int(fragCoord.y);

    int g = gcd(a, b);
    int m = max(a, b);

    float alpha = float(g) / float(m);

    if (g == 1) {
        fragColor = vec4(1., 0., 0., 1.);
    } else {
        fragColor = vec4(vec3(1., 1., 1.) * alpha, 1.);
    }


}
