float Hash21(vec2 p) {
			p = fract(p* vec2(123.34 , 456.21));
			p += dot (p, p + 45.68);
			return fract (p.x * p.y);

			} 



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord * 2. - iResolution.xy)/iResolution.y;
    uv.x += iTime * .1;
    uv *= 55.;
    vec2 id = floor(uv);       
    vec3 col = vec3(0.);      
    uv = fract(uv) - .5;
    float size = 0.;

    for(int y= -1 ; y <= 2; y++){
       for(int x= -1; x <=  2; x++){
         vec2 offs = vec2(x , y);
         float n = Hash21(id + offs);      
         float d= 100.;
         d *= size + sin(iTime * size * .005);
         //d = .2 /d;
         size = fract( n * 232.32);
         d *= size + sin(iTime * size);
         col += vec3(d);  
         col = mix(vec3(0.) ,vec3(.3) , d); 
        }}




   // Output to screen
   fragColor = vec4(col,1.0);
}
