void mainImage(out vec4 fragColor, in vec2 fragCoord ) {
    vec3 rColor = vec3(0.9, 0.0, 0.3);
    vec3 gColor = vec3(0.0, 0.9, 0.3);
    vec3 bColor = vec3(0.0, 0.3, 0.9);
    vec3 yColor = vec3(0.9, 0.5, 0.3);

    vec2 p = (fragCoord.xy * 2.0 - vec2(iResolution.x,iResolution.y));
    p /= min(iResolution.x, iResolution.y);

    float a = sin(p.y * 1.5 - iTime * 0.1) / 1.0;
    float b = tan(p.y * 1.5 - iTime * 0.2) / 1.0;
    float c = sin(p.y * 1.5 - iTime * 0.3 + 3.14) / 1.0;
    float d = cos(p.y * 1.5 - iTime * 0.5 + 3.14) / 1.0;

    float intesity = sin(p.y * 0.5 - iTime * 0.75 + 3.14) / 7.0;

    float e = intesity + 0.05 / abs(p.x + a);
    float f = 0.09 / abs(p.x + b);
    float g = 0.09 / abs(p.x + c);
    float h = 0.09 / abs(p.x + d);

    vec3 destColor = rColor * e + gColor * f + bColor * g + yColor * h;
    fragColor = vec4(destColor, 1.0);
}
