void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 uv0 = uv*uv.y;
    uv.y -= (0.5*iTime);
    float x = texture(iChannel0, 0.2*uv).x;
    float y = 1. - uv0.y;

    vec4 yellow = vec4(1.0, 0.65, 0.1, 0.25);
    vec4 red = vec4(1.0, 0.0, 0.15, 0.0625);
    vec4 orange = mix(yellow, red, 0.5);


    vec4 col = vec4(0.);
    float c0 = step(x, y);
    float c1 = c0 - step(x, y-0.1);
    col = mix(yellow, red, c1);
    float c2 = step(x, y-0.1) - step(x, y-0.2);
    col = mix(col, orange, c2);
    col = mix(vec4(0.), col, c0);

    // Output to screen
    fragColor = (col);
}
