// Distance to an oriented box.

// List of some other 2D distances: https://www.shadertoy.com/playlist/MXdSRf
//
// and iquilezles.org/articles/distfunctions2d

float udSegment( in vec2 p, in vec2 a, in vec2 b )
{
    vec2 ba = b-a;
    vec2 pa = p-a;
    float t = dot(pa,ba)/dot(ba,ba);
    t =clamp(t, 0.0, 1.0 );
    return length(pa-t*ba);
}


float halfspace(in vec2 q, float th)
{
    vec2 ba = vec2(0, -2.0 * th);
    vec2 qa = q-vec2(0, -th);
    float t = dot(qa,ba)/dot(ba,ba);
    return sign(q.x) * length(qa-t*ba);
}

float halfspace2(vec2 q, float th, float drawSectionLen)
{
    float near = halfspace(q, th);
    q.x = -q.x-drawSectionLen;
    float far = halfspace(q, th);
    return max(near,far);
}

float halfspace(vec2 q, float th, float drawSectionLen)
{
    float near = sign(q.x) * udSegment(q, vec2(0,-th), vec2(0,+th));
    q.x = -q.x-drawSectionLen;
    float far = sign(q.x) * udSegment(q, vec2(0,-th), vec2(0,+th));
    return max(near,far);
}

float sdOrientedBox( in vec2 p, in vec2 a, in vec2 b, float th )
{
    float roundedDist = udSegment(p, a, b) - th;

#define ROUNDED1
#ifdef ROUNDED
    return roundedDist;
#else
    float l = length(b-a);
    vec2  d = (b-a)/l;
    vec2  q = p-a;
          q = mat2(d.x,-d.y,d.y,d.x)*q;

    // whatever doesn't matter, its just the value up to which the SDF needs to be accurate
    float aawidth = th*0.6;

    float betweenTwoLines = -696969696969.69f;
    // this if-else arises naturally, information about `l` is not needed, just `aawidth`
    if (q.x<aawidth)
        betweenTwoLines = halfspace(q*vec2(-1.0,1.0),th,l);
    else if (q.x>l-aawidth)
        betweenTwoLines = halfspace(q-vec2(l,0.0),th,l);

    roundedDist = max(roundedDist, betweenTwoLines);
    // clamp is not needed just illustrating invariance of result
    return clamp(roundedDist,-aawidth,aawidth);
#endif
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // normalized pixel coordinates
    vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;
    vec2 m = (2.0*iMouse.xy-iResolution.xy)/iResolution.y;
    p *= 1.4;
    m *= 1.4;

#if 0
    // animation
    vec2 v1 = cos( iTime*0.5 + vec2(0.0,1.00) + 0.0 );
	vec2 v2 = cos( iTime*0.5 + vec2(0.0,3.00) + 1.5 );
    float th = 0.3*(0.5+0.5*cos(iTime*1.1+1.0));
#else
    // animation
    vec2 v1 = vec2(-abs(cos(iTime))*0.0,0.0);
	vec2 v2 = vec2(abs(cos(iTime))*1.5,0.0);
    float th = 0.3;
#endif

	float d = sdOrientedBox( p, v1, v2, th );

    // distance
    vec3 col = vec3(1.0) - sign(d)*vec3(0.1,0.4,0.7);
	col *= 1.0 - exp(-6.0*abs(d));
	col *= 0.8 + 0.2*cos(120.0*d);
	col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,0.015,abs(d)) );

    if( iMouse.z>0.001 )
    {
    d = sdOrientedBox(m, v1, v2, th);
    col = mix(col, vec3(1.0,1.0,0.0), 1.0-smoothstep(0.0, 0.005, abs(length(p-m)-abs(d))-0.0025));
    col = mix(col, vec3(1.0,1.0,0.0), 1.0-smoothstep(0.0, 0.005, length(p-m)-0.015));
    }


	fragColor = vec4(col, 1.0);
}
