vec2 grad(vec2 uv) {
    // Compute the gradient of the. channel used for shading
    return vec2(0,0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 g = grad(uv);
    vec3 t1 = vec3(1,0,20.0*g.x);
    vec3 t2 = vec3(0,1,20.0*g.y);
    vec3 N = normalize(cross(t1,t2));
    vec3 L = normalize(vec3(1,3,5));
    float illum = (0.4+0.4*smoothstep(0.97,0.98, dot(N,L)) + 0.2 * max(0.0, dot(N,L)));

    float substance = 2.0*texture(iChannel0, uv).g;
    // Output to screen
    fragColor = illum*vec4(0.3, 0.7, 0.5,0.0) + (1.0-substance)*vec4(0.6,0.4,0.1, 0.0);

   //fragColor = vec4(1.0)-vec4(1.0,1.,1.,0.0)*texture(iChannel0, uv).g;
}

vec4 lap(vec2 uv) {
    // Compute the Laplacian of all four elements of iChannel0

    float dx = 1. / iResolution.x;
    float dy = 1. / iResolution.y;

    vec4 res = vec4(0);

    res = (1. / 4.) * (texture(iChannel0, vec2(uv.x+dx, uv.y)) +
                          texture(iChannel0, vec2(uv.x-dx, uv.y)) +
                          texture(iChannel0, vec2(uv.x, uv.y+dy)) +
                          texture(iChannel0, vec2(uv.x, uv.y-dy)) -
                      4. * texture(iChannel0, uv));

    return res;
}

float sdCircle( vec2 p, float r0, float r1)
{
    return abs(length(p - vec2(0.5, 0.5)) - r0) - r1;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec4 BufferA = texture(iChannel0, uv);

    // Suggested time step
    float dt = .666;

    // Mitosis
    float Da = 1.0;
    float Db = 0.5;
    float k = 0.065;
    float f = 0.0367;

    float A = texture(iChannel0, uv).r; // Substance A
    float B = texture(iChannel0, uv).g; // Substance B

    // Reaction Diffusion
    float Ap = A + (Da * lap(uv).x  - A*B*B + f * (1. - A)) * dt;
    float Bp = B + (Db * lap(uv).y  + A*B*B - (k + f) * B) * dt;

    Ap = clamp(Ap, 0., 1.);
    Bp = clamp(Bp, 0., 1.);



    vec2 V = fragCoord - iMouse.zw;
    if (iTime < .05 ) {
        fragColor = vec4(0,0,0,0);

    } else {
        fragColor = exp(-.75*dot(V,V))*vec4(0,1.0,0,0) + vec4(Ap, Bp, 0.0, 0.0);
    }
}
