void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 normalMouse = iMouse.xy/iResolution.xy;
    if(iMouse.xy == vec2(0,0)) normalMouse.y = 0.5;
    float anim = fract((uv.x + iTime / 6.) / 1.5 );

    vec4 colours[] = vec4[](
        vec4(1.0, 0.4863, 0.4667, 1.0),
        vec4(1.0, 0.9725, 0.4667, 1.0),
        vec4(0.5098, 1.0, 0.4627, 1.0),
        vec4(0.4667, 0.9725, 1.0, 1.0),
        vec4(0.5137, 0.4667, 1.0, 1.0),
        vec4(1.0, 0.4627, 0.8941, 1.0),
        vec4(1.0, 0.2196, 0.1922, 1.0),
        vec4(1.0, 0.9608, 0.1922, 1.0),
        vec4(0.2588, 1.0, 0.1922, 1.0),
        vec4(0.2, 0.9608, 1.0, 1.0),
        vec4(0.251, 0.1843, 1.0, 1.0),
        vec4(1.0, 0.1882, 0.8392, 1.0)
    );

    vec4 gradient = vec4(0);
    for(int i = 0; i < colours.length(); i++){
        float i_f = float(i);
        float len_f = float(colours.length());

        float _step = i_f/len_f;
        float _step2 = (i_f+1.)/len_f;

        bool smoothMix = false;
        if(uv.y > normalMouse.y){
            smoothMix = true;
        }

        float len = float(colours.length());
        for(int i = 0; i < colours.length(); i++){
            float _step = smoothMix ? float(i)/len : (float(i)+0.5)/len;
            float _step2 = smoothMix ? (float(i)+1.)/len : (float(i)-0.5)/len;

            gradient = mix(
                i == 0 ? colours[colours.length()-1] : gradient,
                colours[i],
                smoothMix ? smoothstep(_step, _step2, anim) : step(_step, anim)
            );
        }
    }

    fragColor = gradient;
}
