#define DURATION 7.
#define TILES 10.

float invLerp(float from, float to, float value){
  return value - from;
}

float remap(float o1, float o2, float t1, float t2, float v){
  return mix(t1, t2, v - o1);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = iTime/DURATION;

    vec2 uv = fragCoord/iResolution.xy;
    uv.x += 0.1*uv.y;
    uv.x += sin(uv.y*5.)*0.01;

    vec2 scale = vec2(TILES, 1.);
    vec2 st = fract(uv*scale);
    float id = floor((uv*scale).x);

    vec3 col = texture(iChannel1, uv).bbb;
    vec3 fillColor = texture(iChannel0, uv).rgb;

    float k = fract(time)*(scale.x+2.);

    if(id < floor(k)) {
        col = fillColor;
    }

    if(id == floor(k)) {
        float localTime = remap(0., 1., -1., 2., fract(k));
        float up = uv.y-localTime;
        float down = (1.-uv.y)-localTime;
        float c = mix(up, down, mod(id, 2.));
        col = mix(col, fillColor, smoothstep(0.5, 0., c));
    }

    fragColor = vec4(col,1.0);
}
