vec4 average_blur( vec2 fragCoord, float kernel_size )
{
    vec4 col = vec4(0.);
    float conv_size = kernel_size * kernel_size;
    for (float i = -(kernel_size - 1.) / 2.; i <= (kernel_size - 1.) / 2.; i+=1.)
    {
        for (float j=-(kernel_size - 1.) / 2.;j<= (kernel_size - 1.) / 2.;j+=1.)
        {
            col += texture( iChannel0, vec2( fragCoord.x + i, fragCoord.y + j) / iResolution.xy ) / conv_size;
        }
    }
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float kernel_size = floor( 1. + iMouse.x / 100. ) * 2. + 1.;
    fragColor = average_blur( fragCoord, kernel_size );
}
