renderpass.code:
M -= T(C).xy);
    vec2 L = vec2(8, 0);
    vec4 a = T(C+L),
         b = T(C-L),
         c = T(C+L.yx),
         d = T(C-L.yx),
         B = b-a, D = d-c;
    R +=  vec4( vec2(B.z,D.z)/2. + (length(C - 1e2) < 50. ? .01 : 0. ),
                ((a + b + c + d) / 4. + (B.x + D.y) / 8. - R ).z,
                ( cos(R+R) - R.w ) *.01
               );
}
