vec3 palette(float t) {
 vec3 a = vec3(0.5);
 vec3 b = vec3(0.5);
 vec3 c = vec3(1.0);
 vec3 d = vec3(0.06, 0.392, 0.725);

 return a + b*cos( 6.28318*( c*t + d ) );
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
 vec2 uv = (fragCoord * 2.0 - iResolution.xy)/iResolution.y;
 vec3 col = vec3(0);

 uv *= 16.0;

 float g_cd = length(uv);

 uv = fract(uv) - 0.5;

 float cd = length(uv);
 float d = cd*cos(2.0*uv.x)*cos(uv.y);
 d = 0.8+0.8*sin(4.0*d + 5.0*iTime + 0.5*g_cd);
 d = abs(d);
 d = step(d, g_cd*0.1 + 0.5*sin(1.5*iTime + uv.x*uv.y));

 col += d;

 col *= mix(palette(cd + g_cd*0.1 - 1.5*iTime), vec3(0), step(16.0, g_cd));

 fragColor = vec4(col,1.0);
}
