float circleSDF(vec2 p, vec2 center, float radius) {
    return length(p - center) - radius;
}

float frogSDF(vec2 p) {
    float body = circleSDF(p, vec2(0.0, -0.2), 0.3);
    float head = circleSDF(p, vec2(0.0, 0.15), 0.15);
    float leftEye = circleSDF(p, vec2(-0.1, 0.25), 0.05);
    float rightEye = circleSDF(p, vec2(0.1, 0.25), 0.05);

    float legs = min(
        circleSDF(p, vec2(-0.2, -0.4), 0.1),
        min(
            circleSDF(p, vec2(0.2, -0.4), 0.1),
            min(
                circleSDF(p, vec2(-0.45, -0.45), 0.07),
                circleSDF(p, vec2(0.45, -0.45), 0.07)
            )
        )
    );

    return min(min(min(body, head), min(leftEye, rightEye)), legs);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy / iResolution.xy) * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    float sdf = frogSDF(uv);
    float edgeWidth = 0.005;
    float edge = smoothstep(-edgeWidth, edgeWidth, sdf);
    vec3 color = mix(vec3(0.0, 0.6, 0.0), vec3(1.0), edge);

    fragColor = vec4(color, 1.0);
}
