#define NO_OF_COLORS 7
#define MAGIC_SMOOTHER 0.0618

vec3 createGradient(in vec3 color, in vec3 colors[NO_OF_COLORS], in float steps[NO_OF_COLORS],in float y ) {
    for (int i = 1; i < NO_OF_COLORS; i++) {
      color = mix(color, colors[i], smoothstep(steps[i-1], steps[i]+MAGIC_SMOOTHER, y+MAGIC_SMOOTHER)); 
    }
    return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float y = uv.y;  

    vec3 colors[NO_OF_COLORS];
    colors[0] = vec3(232,218,160)/255.;
    colors[1] = vec3(235,227,167)/255.;
    colors[2] = vec3(241,244,181)/255.;
    colors[3] = vec3(218,234,202)/255.;
    colors[4] = vec3(189,221,231)/255.;
    colors[5] = vec3(172,213,248)/255.;
    colors[6] = vec3(166,211,255)/255.;

    float steps[NO_OF_COLORS];
    steps[0] = 0.05;
	steps[1] = 0.15;
    steps[2] = 0.27;
	steps[3] = 0.38;
	steps[4] = 0.57;
    steps[5] = 0.73;
    steps[6] = 0.84;

    // startColor
    vec3 color = colors[0];

    // Output to screen
    fragColor = vec4(createGradient(color,colors,steps,y),1.);
}


