void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float t = iTime*0.8;
    float PI = 3.14159;

    uv.y +=uv.y-0.0; //look offset y axes
    uv.x *=uv.x+2.0; //look offset x axes
    uv.x *=uv.x*floor(1.0); //look offset y axes, ditto
    uv.y *=uv.x/floor(1.0); //look offset x axes, ditto

    uv.x +=uv.y*cos(1.5); //perspective offset
    uv.y -=uv.x*sin(1.5); //

    uv.y += tanh(sin(0.1*sin(0.1)+t))/PI; //pan animation
    uv.x -= tanh(0.1*cos(0.3*t)*6.0)*PI;  //

    if(mod(floor(uv.x * 12.0), 2.0) == mod(floor(uv.y * 8.0), 2.0)) { //grid draw setup

        fragColor = vec4(0.1, 0.8, 0.1, 1.0) + vec4(0.1, 0.8, 0.1, 1.0);
    } else {
		fragColor = vec4(0.28, 0.28, 0.3, 1.0);
    }
}


