void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
vec2 p = (fragCoord.xy / iResolution.xy) * 2.0 - 1.0;

float t = iTime * 0.5;

p.y += sin(p.x * 16.0 + t * 2.5) * cos(p.x * 2.0 + t * 2.5) * 0.4 * 0.2;
p.x += cos(p.y * 8.0 + t * 2.5) * sin(p.x * 12.0 + t * 2.5) * 1.2 * 0.2;
p.x += sin(p.y * 2.0 + t * 2.5) * cos(p.x * 64.0 + t * 2.5) * 1.2 * 0.2;

float distance = length(p);

float v = smoothstep(0.0, 1.0, 1.0 - distance);
vec3 col = vec3(1.0, 1.0, 1.0) * v;

col.x += sin(p.x * 10.0 + t * 2.5);
col.y += sin(p.x * 10.55 + t * 2.5);
col.z += sin(p.x * 11.0 + t * 2.5);

fragColor = vec4(col*0.65, 1.0);
}
