// Fork of "" by None. https://shadertoy.com/view/-1
// 2023-04-05 14:09:55

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y*3.0;

    vec2 uv_tige = uv;

    // Rotation de la fleur
    float rotationSpeed = 0.3; // Vitesse de rotation
    float angle = iTime * rotationSpeed;
    float s = sin(angle);
    float c = cos(angle);
    uv = vec2(uv.x * c - uv.y * s, uv.x * s + uv.y * c);

    // Calcul des coordonnées polaires pour créer la forme de la fleur
    float r = length(uv);
    float theta = atan(uv.y, uv.x);
    //le contour
    float body = cos(theta * 8.0) + 0.2;

    float bord = sin((theta) * 8.0)/2.5 + 0.2;
    vec3 color = mix(vec3(0.620,0.780,0.690), vec3(0.647,0.812,0.710), body);

    float edge = smoothstep(0.3, 0.32, abs(r - bord));
    color *= edge;

    float aile = smoothstep(0.3, 0.05, length(uv - vec2(-0.1, 0.1)));
    aile += smoothstep(0.3, 0.5, length(uv - vec2(-0.5, -0.1)));

    // Ajout d'un arrière-plan de couleur
    vec3 backgroundColor = vec3(0.859,0.078,0.078);
    color = mix(backgroundColor, color, edge);

    //Ajout de la tige
    float tige = smoothstep(0.05, 0.005, abs(uv_tige.x));
    tige *= smoothstep(0.03, -0.01, uv_tige.y);
    color = mix(color, vec3(0.106,0.094,0.094), tige);

    // Ajout du soleil
    vec2 sunPos = vec2(1.9, 1.0); // Position du soleil dans le coin supérieur droit
    float sunRadius = 0.35;
    float sunEdge = smoothstep(sunRadius, sunRadius - 0.01, length(uv_tige - sunPos));
    color = mix(color, vec3(1.0, 1.0, 0.0), sunEdge);

    fragColor = vec4(color,1.0);
}
