#define PI 3.1415926535897932384626433832795
#define M_PI 0.523598776

#define cx_mul(a, b) vec2(a.x*b.x - a.y*b.y, a.x*b.y + a.y*b.x)
#define cx_scal_mul(a, b) vec2(a.x*b, a.y*b)
#define cx_div(a, b) vec2(((a.x*b.x + a.y*b.y)/(b.x*b.x + b.y*b.y)),((a.y*b.x - a.x*b.y)/(b.x*b.x + b.y*b.y)))
#define cx_sin(a) vec2(sin(a.x) * cosh(a.y), cos(a.x) * sinh(a.y))
#define cx_cos(a) vec2(cos(a.x) * cosh(a.y), -sin(a.x) * sinh(a.y))

vec2 cx_tan(vec2 a) {return cx_div(cx_sin(a), cx_cos(a)); }
vec2 cx_log(vec2 a) {
    float rpart = sqrt((a.x*a.x)+(a.y*a.y));
    float ipart = atan(a.y,a.x);
    if (ipart > PI) ipart=ipart-(2.0*PI);
    return vec2(log(rpart),ipart);
}

vec2 as_polar(vec2 z) {
  return vec2(
    length(z),
    atan(z.y, z.x)
  );
}
vec2 cx_pow(vec2 v, float p) {
  vec2 z = as_polar(v);
  return pow(z.x, p) * vec2(cos(z.y * p), sin(z.y * p));
}

float im(vec2 z) {
  return ((atan(z.y, z.x) / PI) + 1.0);
}

vec3 hsv2rgb(vec3 c) {
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

vec3 cx_col(vec2 cpx) {
    float hue = cx_log(cpx).y / (2.0 * 3.14159265);
    float brightness = 1.0;
    vec3 hsv = vec3(hue, 1.0, brightness); 
    return hsv2rgb(hsv);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (fragCoord.xy - 0.5 * iResolution.xy) / min(iResolution.y, iResolution.x);
    vec2 mouse = 5.0 * (iMouse.xy - 0.5 * iResolution.xy) / min(iResolution.y, iResolution.x);
    vec2 z = uv * 5.0;

    vec2 one = vec2(1.0, 0.0);

    vec2 g1 = mouse;
    vec2 g2 = vec2(0.0, 1.0);

    vec2 prod = cx_mul(z,z);
    vec2 t1 = cx_div(one, prod);
    vec2 t2 = vec2(1.0, 0.0);
    vec2 lam;
    for (int i = -10; i < 10; i++) {
        for (int j = -10; j < 10; j++) {
            if (i != 0 || j != 0) {
                lam = cx_scal_mul(g1, float(i)) + cx_scal_mul(g2, float(j));
                t2 = z - lam;
                prod = cx_mul(t2, t2);
                t1 += cx_div(one, prod);
                prod = cx_mul(lam, lam);
                t1 += cx_div(one, prod);
            }
        }
    }

    vec2 result = t1;
    fragColor = vec4(cx_col(result),1.0);
}
